/* eslint-disable */
import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { ArrowUpRightSquare } from 'react-bootstrap-icons';

const Link = ({ data, className }) => {
  const { title, url, imageUrl, description, cardId } = data;
  return (
    <div data-sm-content={cardId} className={className}>
      <div className='card'>
        <div className='d-flex justify-content-center'>
          {/* we can pass either description or imageAltText to alt property */}
          <img src={imageUrl} alt={description} />
        </div>
        <div className='card-body'>
          <h5>{title}</h5>
          <p>{description}</p>
          <div className='d-flex justify-content-center'>
            {/* open link in new tab */}
            <a href={url} className='btn btn-primary' target='_blank' rel='noreferrer'>
              Visit Link
              <ArrowUpRightSquare />
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

Link.propTypes = {
  data: PropTypes.shape({
    url: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    imageUrl: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    cardId: PropTypes.string.isRequired,
    // imageAltText: PropTypes.string,
  }).isRequired,
};

export default styled(Link)`
  width: 20rem;

  img {
    width: auto;
    height: auto;
  }
`;
